            	check = 1;
            }
            
            log.info("#### FRD CHECK: " + check);
            
            
            
        }
        
    }
    log.info("#### FRD CHECK: " + check);
    return check
    
}

function beforeTaskSave(colleagueId, nextSequenceId, userList) {

    GETSLA()
    var ATIVIDADE_ATUAL = getValue('WKNumState');

    if (ATIVIDADE_ATUAL == inicio || ATIVIDADE_ATUAL == 0 || ATIVIDADE_ATUAL == 1 || ATIVIDADE_ATUAL == correcaoSolicitante) {
        var tipoCompra = hAPI.getCardValue('tipoCompra');
        var prioridade = hAPI.getCardValue('prioridadeHidden');
        if (prioridade == 'E') {
            if (tipoCompra == '1') {
                hAPI.setCardValue("COD_SOLICITACAO", '2417719'); //2416905
            } else if (tipoCompra == '2') {
                hAPI.setCardValue("COD_SOLICITACAO", '2417779'); // 2417781
            } else if (tipoCompra == '3') {
                hAPI.setCardValue("COD_SOLICITACAO", '2416942'); //2416906
            } else {
                hAPI.setCardValue("COD_SOLICITACAO", '2417785'); //2417786
            }

            GETSLA()
        }
    }

    if (ATIVIDADE_ATUAL == anexaSolicitacao) {
        // Enviando a qtde de arquivos anexados de pedidos de compras para o campo "anexos".
        var anexo = hAPI.listAttachments();
        hAPI.setCardValue("anexos", hAPI.listAttachments().size());
    }

    if (ATIVIDADE_ATUAL == analiseCompradorCotacao) {
        var qtdAnexos = hAPI.listAttachments();
        if (hAPI.getCardValue("correcaoSolicitacao") != "true") {
            if (qtdAnexos.size() < hAPI.getCardValue("hiddenQtdCotacoes")) {
                throw "É obrigatório anexar as " + hAPI.getCardValue("hiddenQtdCotacoes") + " cotações realizadas.";
            }
            if (hAPI.getCardValue("hiddenChkMinutaContrato") == "true") {
                if (qtdAnexos.size() < parseInt(hAPI.getCardValue("hiddenQtdCotacoes")) + 1) {
                    throw "É obrigatório anexar a minuta do fornecedor.";
                }
            }
            // Se o tipo de Contrato for Novo exige-se no mínimo 4
            if (hAPI.getCardValue("hideNovoContratoOuAdt") == "false" && qtdAnexos < 4) {
                throw "É preciso anexar no minimo 4 arquivos nesta atividade para continuar o processo!";
            }
            // Se o tipo de Contrato for Aditivo exige-se no mínimo 5
            if (hAPI.getCardValue("hideNovoContratoOuAdt") == "true") {
                if ((hAPI.getCardValue("hideAnexoAditivos") == "false" || hAPI.getCardValue("hideAnexoAditivos") == "") && qtdAnexos < 4) {
                    throw "É preciso anexar no mínimo 4 arquivos nesta atividade para continuar o processo!";
                }
                if (hAPI.getCardValue("hideAnexoAditivos") == "true" && qtdAnexos < 5) {
                    throw "É preciso anexar no mínimo 5 arquivos nesta atividade para continuar o processo!";
                }
            }
        }

    }
}

function servicetask109(attempt, message) {
    myLog("SERVICO DE ANEXO");
    myLog("CAMPO PEDIDO: ", hAPI.getCardValue('listPedidoCompra'));
    if (hAPI.getCardValue('listPedidoCompra') == '') {

        throw "Nenhum pedido de compra encontrado no fluig para anexar."

    } else {

        if(hAPI.getCardValue("hidePedidoGuardChuva") == "true"){
            
            var pedidosGerados = JSON.parse(hAPI.getCardValue('listPedidoCompra'));
            for (var nomeFornecedor in pedidosGerados) {

                var splitNomeFornecedor = nomeFornecedor.split("_");
                var codFilial = splitNomeFornecedor[2];

                log.info("ENTREI NO FOR DO pedidosGerados Guardachuva: " + pedidosGerados);
                if (!existeAnexoByName("Pedido_Compras_" + nomeFornecedor + ".pdf")) {

                    log.info("ENTREI NO IF DA NEGATIVA existeAnexoByName: " + nomeFornecedor);
                    anexaPdfFluigGuardaChuva(pedidosGerados[nomeFornecedor], nomeFornecedor, codFilial);
                }
            }

        }else{
            var pedidosGerados = JSON.parse(hAPI.getCardValue('listPedidoCompra'));
            for (var nomeFornecedor in pedidosGerados) {

                log.info("ENTREI NO FOR DO pedidosGerados: " + pedidosGerados);
                if (!existeAnexoByName("Pedido_Compras_" + nomeFornecedor + ".pdf")) {

                    log.info("ENTREI NO IF DA NEGATIVA existeAnexoByName: " + nomeFornecedor);
                    anexaPdfFluig(pedidosGerados[nomeFornecedor], nomeFornecedor);
                }
            }
        }
        
    }
}

function anexaPdfFluig(codPedido, nomeFornecedor) {
    //BUSCA O ANEXO DO PEDIDO
    var codSolicitacao = hAPI.getCardValue('codSolicitacao');
    var codFilial = hAPI.getCardValue('codFilial');
    var resultPdfProtheus = getPdfPedidoProtheus(codPedido, codFilial);
    myLog('RESULT PDF ', resultPdfProtheus.result);
    if (resultPdfProtheus.status != 'true') {
        throw "Erro para buscar o realtório do pedido de compras no Protheus \n" + resultPdfProtheus.result;
    } else {
        myLog("PARM ENVIA ANEXO: ", codSolicitacao + nomeFornecedor);
        //ENVIA O ANEXO PARA O FLUIG
        var statusAnexo = enviaAnexoSolicitacao(codSolicitacao, anexaSolicitacao, resultPdfProtheus.result, nomeFornecedor + " - " + codFilial + " - " + codPedido);
        myLog('RESULT ANEXO FLUIG ', statusAnexo);
        if (statusAnexo != true) {
            throw statusAnexo + ".";
        } else {
            myLog("PEDIDO GERADO: ", codPedido);
        }
    }
}

function anexaPdfFluigGuardaChuva(codPedido, nomeFornecedor, codFilialPedido) {
    //BUSCA O ANEXO DO PEDIDO
    var codSolicitacao = hAPI.getCardValue('codSolicitacao');
    var codFilial = codFilialPedido;
    var resultPdfProtheus = getPdfPedidoProtheus(codPedido, codFilial);
    myLog('RESULT PDF GUARDA CHUVA', resultPdfProtheus.result);
    if (resultPdfProtheus.status != 'true') {
        throw "Erro para buscar o realtório do pedido de compras no Protheus \n" + resultPdfProtheus.result;
    } else {
        myLog("PARM ENVIA ANEXO: ", codSolicitacao + nomeFornecedor);
        //ENVIA O ANEXO PARA O FLUIG
        var statusAnexo = enviaAnexoSolicitacao(codSolicitacao, anexaSolicitacao, resultPdfProtheus.result, nomeFornecedor + " - " + codPedido);
        myLog('RESULT ANEXO FLUIG ', statusAnexo);
        if (statusAnexo != true) {
            throw statusAnexo + ".";
        } else {
            myLog("PEDIDO GERADO: ", codPedido);
        }
    }
}

function existeAnexoByName(nomeAnexo) {
    var attachments = hAPI.listAttachments();
    var existeAnexo = false;
    var nomePossivelAnexo = nomeAnexo;
    for (var i = 0; i < attachments.size(); i++) {
        var attachment = attachments.get(i);
        if (attachment.getDocumentDescription() == nomePossivelAnexo) {
            existeAnexo = true;
        }
    }
    return existeAnexo;
}

