                log.info("#### TABELA FORNECEDOR + CODIGO DO FORNECEDOR VENCEDOR: " + arrayCodFornVencedor);
                log.info("#### hAPI.getCardValue('codForn___' + index) " + hAPI.getCardValue('codForn___' + index));

                for (var j in arrayCodFornVencedor) {
