                        fieldCampo52.getItem().add(hAPI.getCardValue('dvAgenciaForn___' + index));
                        cardData.getItem().add(fieldCampo52);
                    }
                }
            }

            // TABELA FILIAL PARA CONTRATOS GUARDA-CHUVAS
            if (contratoGuardChuva == "on" && field.indexOf("filialGuardChu___") > -1) {
                index = field.split("filialGuardChu___")[1];

                log.info("#### INDEX FILIAL GUARDA-CHUVA: " + index);

                var fieldCampoGuardaChuva1 = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.StringArrayArray');
                fieldCampoGuardaChuva1.getItem().add("filialGuardChu___" + index);
                fieldCampoGuardaChuva1.getItem().add(hAPI.getCardValue('filialGuardChu___' + index));
                cardData.getItem().add(fieldCampoGuardaChuva1);

                var fieldCampoGuardaChuva2 = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.StringArrayArray');
                fieldCampoGuardaChuva2.getItem().add("cnpjFilial___" + index);
                fieldCampoGuardaChuva2.getItem().add(hAPI.getCardValue('cnpjFilial___' + index));
                cardData.getItem().add(fieldCampoGuardaChuva2);

                var fieldCampoGuardaChuva3 = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.StringArrayArray');
                fieldCampoGuardaChuva3.getItem().add("enderecoFilial___" + index);
                fieldCampoGuardaChuva3.getItem().add(hAPI.getCardValue('enderecoFilial___' + index));
                cardData.getItem().add(fieldCampoGuardaChuva3);

                var fieldCampoGuardaChuva4 = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.StringArrayArray');
                fieldCampoGuardaChuva4.getItem().add("valorUnitario___" + index);
                fieldCampoGuardaChuva4.getItem().add(formatMoney(parseFloat(removeMascaraMonetaria(hAPI.getCardValue('valorTotalSolicitacao')) * (parseInt(hAPI.getCardValue('valorUnitario___' + index)) / 100))));
                cardData.getItem().add(fieldCampoGuardaChuva4);

                var fieldCampoGuardaChuva5 = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.StringArrayArray');
                fieldCampoGuardaChuva5.getItem().add("codFilial___" + index);
                fieldCampoGuardaChuva5.getItem().add(hAPI.getCardValue('codFilialGuardChu___' + index));
                cardData.getItem().add(fieldCampoGuardaChuva5);

                var fieldCampoGuardaChuva6 = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.StringArrayArray');
                fieldCampoGuardaChuva6.getItem().add("hideContratoGuardChuva");
                fieldCampoGuardaChuva6.getItem().add(hAPI.getCardValue('hideContratoGuardChuva'));
                cardData.getItem().add(fieldCampoGuardaChuva6);

                var fieldCampoGuardaChuva7 = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.StringArrayArray');
                fieldCampoGuardaChuva7.getItem().add("valorPercent___" + index);
                fieldCampoGuardaChuva7.getItem().add(hAPI.getCardValue("valorUnitario___" + index));
                cardData.getItem().add(fieldCampoGuardaChuva7);
            }
        }

        // SERVIÇO DO STARTPROCESS
        log.info("$$$$$$$$$$ StartProcess solicitacaoNovoContratoAditivos!!! $$$$$$$$$$");
        log.dir(cardData);
        var rest = workflowEngineService.startProcess("integrador.fluig@oncoclinicas.com",
            "hUbust*7",
            1,
            "solicitacaoNovoContratoAditivos",
            0,
            stringArray,
            "Solicitação iniciada através do Fluxo de Compras Diversos (Nº " + numProcesso + ").",
            idSolicitante,
            true,
            processAttachmentDtoArray,
            cardData,
            processTaskAppointmentDtoArray,
            false);

        var iProcess = "";
        for (var j = 0; j < rest.getItem().size(); j++) {
            var item = rest.getItem().get(j).getItem();
            var key = item.get(0);
            var value = item.get(1);

            log.info("### key formulários contratos: " + key);
            log.info("####### Número da Solicitação aberta em contratos: " + value);

            if (key == "iProcess") {
                iProcess = value;

                log.info("####### iProcess: " + iProcess);
                hAPI.setCardValue('numSolicitAbertaFilho', value);
                log.info("####### numSolicitAbertaFilho " + hAPI.getCardValue('numSolicitAbertaFilho'));

                // ADD O NÚMERO DA NOVA SOLIITAÇÃO NOS COMPLEMENTOS
                var usuario = hAPI.getCardValue('idSolicitante'); //getValue('WKUser');
                var mensagem = "Aberto a solicitação de Contratos através do Nº " + value;

                hAPI.setTaskComments(usuario, numProcesso, 0, mensagem);
                log.info("####### insere MGS - " + usuario + " - " + numProcesso + " - " + mensagem);
            }
        }

        if (iProcess == "") {
            log.info(numProcesso);
            throw "Erro na abertura da solicitação";
        }

    } catch (e) {
        throw "Ocorreu um erro ao tentar criar uma solicitação de Contratos " + e;
    }

}

function servicetask170(attempt, message) {

    log.info("###--- Entrei no envio de e-mail cancelamento de compras.");
    var emailSolicitante = hAPI.getCardValue('emailSolicitante');
    var emailComprador = hAPI.getCardValue('emailComprador');
    log.info("###--- emailSolicitante" + emailSolicitante);
    log.info("###--- emailComprador" + emailComprador);

    if (emailSolicitante != "") {
        log.info("###--- Entrei no if solicitante");
        enviaEmail(emailSolicitante);
    }
    if (emailComprador != "") {
        log.info("###--- Entrei no if comprador");
        enviaEmail(emailComprador);
    }
}

/**
 * Envia email de credenciais de acesso de rede
 */
function enviaEmail(email) {
    log.info("###--- Entrei na funcão");
    var sender = "4s2f7mmb7dfs64qv1452799368975";
    try {
        //Parametros de envio
        var parameters = new java.util.HashMap();
        parameters.put("subject", "Notificação de Cancelamento de Chamado [" + hAPI.getCardValue("codSolicitacao") + "]");
        parameters.put("COD_SOLICITACAO", hAPI.getCardValue("codSolicitacao"));
        //Seta os destinatarios
        var recipients = new java.util.ArrayList();
        recipients.add(email);
        notifier.notify(sender, "email_cancelamento", parameters, recipients, "text/html");

    } catch (e) {
        log.info("Ocorreu um erro ao tentar enviar o e-mail de cancelamento compras" + e);
    }
}

function afterTaskSave(colleagueId, nextSequenceId, userList) {

    var ATIVIDADE_ATUAL = getValue('WKNumState');

    hAPI.setCardValue("codSolicitacao", getValue("WKNumProces"));
    hAPI.setCardValue("hiddenQtdAnexos", hAPI.listAttachments().size());
              
    if (ATIVIDADE_ATUAL == 14) {
        var achou = false;
        var processo = getValue("WKNumProces");
        var campos = hAPI.getCardData(processo);
        var contador = campos.keySet().iterator();

        while (contador.hasNext()) {
            var id = contador.next();
            if (id.match(/codProd___/)) {
                var campo = campos.get(id);
                var seq = id.split("___")[1];
                var produto = campos.get("codProd___" + seq);

                var c1 = DatasetFactory.createConstraint("codProduto",produto, produto, ConstraintType.MUST);
                var filtro = new Array(c1);
                var dataset = DatasetFactory.getDataset("ds_servicos_obras_ti", null, filtro, null);
                log.info("TAMANHO DATASET" + dataset.rowsCount);
                if(dataset.rowsCount > 0){
                    for(var i = 0; i < dataset.rowsCount; i++) {
    
                        achou = true;
                        log.info("ACHOU ANTES" + achou);
    
                    }
                } 
            } 

        }
        log.info("ACHOU DEPOIS" + achou);

        if (achou == true) {
            enviaCorpoEmail();
        }

    }

    if (ATIVIDADE_ATUAL == analiseCompradorCotacao) {
        //Adiciona os motivos de reprovação nos comentarios da solicitação
        if (hAPI.getCardValue("correcaoSolicitacao") == "true") {
            hAPI.setTaskComments(getValue("WKUser"), getValue("WKNumProces"),
                0, "Solicitação reprovada pelo Comprador! \n" + hAPI.getCardValue("motivoAnaliseComprador"));
        }

    }

    if ((nextSequenceId == 11 || nextSequenceId == '11') && hAPI.getCardValue("decisaoSolicitante") != "sim") {
        notificacaoEncerramento(hAPI.getCardValue("emailSolicitante"), hAPI.getCardValue("nomeSolicitante"));
    }

    if (ATIVIDADE_ATUAL == alcadaAprovacaoCarta) {
        var proximaAlcada = parseInt(hAPI.getCardValue("contadorAlcadaCarta")) + 1;
        hAPI.setCardValue("contadorAlcadaCarta", proximaAlcada);
    }
    if (ATIVIDADE_ATUAL == 7) {
        var proximaAlcada = parseInt(hAPI.getCardValue("contadorAlcada")) + 1;
        hAPI.setCardValue("contadorAlcada", proximaAlcada);
    }
    if (ATIVIDADE_ATUAL == alcadaAprovacaoPA) {
        var proximaAlcada = parseInt(hAPI.getCardValue("contadorAlcadaPA")) + 1;
        hAPI.setCardValue("contadorAlcadaPA", proximaAlcada);
    }
}

function notificacaoEncerramento(emailSolicitante, solicitante) {
    var filter = new Array();
    filter.push(DatasetFactory.createConstraint('SOLICITACAO', getValue("WKNumProces"), '', ConstraintType.MUST));
    filter.push(DatasetFactory.createConstraint('SOLICITANTE', solicitante, '', ConstraintType.MUST));
    filter.push(DatasetFactory.createConstraint('EMAIL_SOLICITANTE', emailSolicitante, '', ConstraintType.MUST));
    filter.push(DatasetFactory.createConstraint('PROCESSO', getValue("WKDef"), '', ConstraintType.MUST))
    var ds_envioEmailEncerramento = DatasetFactory.getDataset('ds_envioEmailEncerramento', null, filter, null);
}

function enviaCorpoEmail() {

    var filialEmail = hAPI.getCardValue('codFilial');
    var nomeFilialEmail = hAPI.getCardValue('filial');
    var centroCustoEmail = hAPI.getCardValue('codCentroCusto');
    var nomeCentroDeCusto = hAPI.getCardValue('centroDeCusto');
    var localEntregaEmail = hAPI.getCardValue('localEntrega');
    var inicioServicoEmail = hAPI.getCardValue('quandoIniciadoServ');
    
    var codFilial = null;

    //var recipients = [];
    //recipients.push("sesmtcso@oncoclinicas.com");
    
    var recipientsSesmt = [];
    //
  //Cria a constraint para buscar os formulários ativos
    var c_active = DatasetFactory.createConstraint("metadata#active", true, true, ConstraintType.MUST);
    var parameters = new Array();
    parameters.push(c_active);
            
    if (codFilial != null) {
    	var c_codFilial = DatasetFactory.createConstraint("codFilial", filialEmail, filialEmail, ConstraintType.MUST);
    	parameters.push(c_codFilial);
    }        	    	    	    
     
    var datasetPrincipal = DatasetFactory.getDataset("ds_emails_sesmet", null, parameters, null);
    
    log.info("numero de linhas: "+datasetPrincipal.rowsCount);
    												  
	if (datasetPrincipal != null && datasetPrincipal.rowsCount > 0) {
    	for (var i = 0; i < datasetPrincipal.rowsCount; i++) {
            var documentId = datasetPrincipal.getValue(i, "metadata#id");
            var documentVersion = datasetPrincipal.getValue(i, "metadata#version");            
                    
            //Cria as constraints para buscar os campos filhos, passando o tablename, número da formulário e versão
            var constraintsFilhos = new Array();				  
            var c1 = DatasetFactory.createConstraint("tablename", "tbColaboradores" ,"tbColaboradores", ConstraintType.MUST);
            var c2 = DatasetFactory.createConstraint("metadata#id", documentId, documentId, ConstraintType.MUST);
            var c3 = DatasetFactory.createConstraint("metadata#version", documentVersion, documentVersion, ConstraintType.MUST);
            var c4 = DatasetFactory.createConstraint("filialUser", filialEmail, filialEmail, ConstraintType.MUST);
            constraintsFilhos.push(c1);
            constraintsFilhos.push(c2);
            constraintsFilhos.push(c3);
            constraintsFilhos.push(c4);
                   
            //Busca o dataset
            var datasetFilhos = DatasetFactory.getDataset("ds_emails_sesmet", null, constraintsFilhos, null);
            
            if (datasetFilhos != null && datasetFilhos.rowsCount > 0) { 
	            for (var j = 0; j < datasetFilhos.rowsCount; j++) {
	            	recipientsSesmt.push(datasetFilhos.getValue(j, 'email')+'');
	            }
            }
        }	
	}
	
	if (recipientsSesmt.length == 0) {		
		recipientsSesmt.push("clelio.junior@oncoclinicas.com");		
	}
		
	   
    var html = "";
    html += '<h2>Prezados(as),</h2>'
    html += '<p>Informo que o chamado <strong>' + getValue('WKNumProces') + '</strong> foi aberto por <strong>' + hAPI.getCardValue('nomeSolicitante') + '</strong>. Segue as informações do chamado: </p>';
    html += '<p><strong>Código da Filial: </strong>'  + nomeFilialEmail + '</p>'
    html += '<p><strong>Centro de Custo: </strong>'  + nomeCentroDeCusto + '</p>'
    html += '<p><strong>Referência do Local: </strong>' + localEntregaEmail + '</p>'
    html += '<p><strong>Data de início das atividades: </strong>' + inicioServicoEmail + '</p>'    

    var processo = getValue("WKNumProces");
    var campos = hAPI.getCardData(processo);
    var contador = campos.keySet().iterator();

    while (contador.hasNext()) {
        var id = contador.next();
        if (id.match(/codProd___/)) {
            var campo = campos.get(id);
            var seq = id.split("___")[1];
            var produto = campos.get("codProd___" + seq);

            var c1 = DatasetFactory.createConstraint("codProduto", produto, produto, ConstraintType.MUST);
            var filtro = new Array(c1);
            var dataset = DatasetFactory.getDataset("ds_servicos_obras_ti", null, filtro, null);

            if(dataset.rowsCount > 0){
               for(var i = 0; i < dataset.rowsCount; i++){

                var descricao = campos.get("nomeProd___" + seq);
                var quantidade = campos.get("unidadeProd___" + seq);
                var seguranca = campos.get("qtdProd___" + seq);

                html += "</tr>";
                html += " <td style='text-align:center'><strong>Tipo de serviço a ser realizado:&nbsp;</strong>" + produto + "</td>";
                html += " <td style='text-align:center'>" + descricao + "</td>";
                html += " <td style='text-align:center'>" + quantidade + "</td>";
                html += " <td style='text-align:center'>" + seguranca + "</td>";
                html += "</tr>";

               }
            }
        } // if while
        html += "</tbody></table>"
    }
 
    var jsonSesmt = {
        assunto: "Serviços Contratados",
        destinatario: recipientsSesmt,
        template: "templateEmailPadrao",
        sender: hAPI.getCardValue("remetente") + "",
        html: html
    }

    
    var consSesmt = DatasetFactory.createConstraint("JSON", JSON.stringify(jsonSesmt), "", ConstraintType.MUST);
    var ds_envioEmailSesmt = DatasetFactory.getDataset("ds_envioEmail", null, [consSesmt], null).values;

        if (ds_envioEmailSesmt [0].SUCESSO != "TRUE") {
            log.info("Erro ds_envioEmail " + getValue("WKNumProces"));    
        }      
   

}

function servicetask272(attempt, message) {
    iniciarSolicitacao();
}

function iniciarSolicitacao() {

    var indiceCadastro = parseInt(hAPI.getCardValue('indiceCadastro'));
    var totalFornSemCadastro = parseInt(hAPI.getCardValue("totalFornSemCadastro"));
    var arrayFornecedor = JSON.parse(hAPI.getCardValue("jsonFornACadastrar"));

    for (indiceCadastro; indiceCadastro < totalFornSemCadastro; indiceCadastro++) {
        log.info('indiceCadstro: ' + indiceCadastro)

        var user = new java.util.ArrayList();
        user.add(arrayFornecedor[indiceCadastro].codSolicitante);

        var formData = new java.util.HashMap();
        formData.put("codSolicitante", arrayFornecedor[indiceCadastro].codSolicitante);
        formData.put("solicitante", arrayFornecedor[indiceCadastro].solicitante);
        formData.put("emailSolicitante", arrayFornecedor[indiceCadastro].emailSolicitante);
        formData.put("fornecedor", arrayFornecedor[indiceCadastro].fornecedor);
        formData.put("cpfCnpjOutros", arrayFornecedor[indiceCadastro].cpfCnpjOutros);
        formData.put("cpf", arrayFornecedor[indiceCadastro].cpf);
        formData.put("cnpj", arrayFornecedor[indiceCadastro].cnpj);
        formData.put("descFilial", arrayFornecedor[indiceCadastro].descFilial);
        formData.put("emailForn", arrayFornecedor[indiceCadastro].emailForn);
        formData.put("endereco", arrayFornecedor[indiceCadastro].endereco);
        formData.put("bairro", arrayFornecedor[indiceCadastro].bairro);
        formData.put("municipio", arrayFornecedor[indiceCadastro].municipio);
        formData.put("uf", arrayFornecedor[indiceCadastro].uf);
        formData.put("codPais", arrayFornecedor[indiceCadastro].codPais);
        formData.put("tipoConta", arrayFornecedor[indiceCadastro].tipoConta);
        formData.put("agencia", arrayFornecedor[indiceCadastro].agencia);
        formData.put("codBanco", arrayFornecedor[indiceCadastro].codBanco);
        formData.put("digVAgencia", arrayFornecedor[indiceCadastro].digVAgencia);
        formData.put("contaCorrente", arrayFornecedor[indiceCadastro].contaCorrente);
        formData.put("digVConta", arrayFornecedor[indiceCadastro].digVConta);
        formData.put("complementoEnd", arrayFornecedor[indiceCadastro].complementoEnd);
        formData.put("cep", arrayFornecedor[indiceCadastro].cep);
        formData.put("ddi", arrayFornecedor[indiceCadastro].ddi);
        formData.put("ddd", arrayFornecedor[indiceCadastro].ddd);
        formData.put("telefone", arrayFornecedor[indiceCadastro].telefone);
        formData.put("tipoFornecedor", arrayFornecedor[indiceCadastro].tipoFornecedor);
        formData.put("localidadeFornecedor", arrayFornecedor[indiceCadastro].localidadeFornecedor);
        formData.put("tipoCadastro", arrayFornecedor[indiceCadastro].tipoCadastro);
        formData.put("cadastroAutomatico", arrayFornecedor[indiceCadastro].cadastroAutomatico);
        formData.put("motivoAprovContasReceber", arrayFornecedor[indiceCadastro].motivoAprovContasReceber);
        formData.put("cpDataAberturaChamado", dataAtual());

        log.info(getValue("WKNumProces") + ' - formdata cadastro fornecedor');
        log.dir(formData);

        var subprocesso = hAPI.startProcess("cadastroFornecedor", 0, user, "Solicitação inicializada a partir da solicitacao " + hAPI.getCardValue("codSolicitacao"), true, formData, false);
        //var numSubProcesso = subprocesso.get("iProcess")
        //log.info(getValue("WKNumProces") + ' - Gerou cadastroFornecedor ' + numSubProcesso);

        // ADD O NÚMERO DA NOVA SOLIITAÇÃO NOS COMPLEMENTOS
        var usuario = hAPI.getCardValue('idSolicitante'); //getValue('WKUser');
        var numProcesso = hAPI.getCardValue("codSolicitacao");
        var mensagem = "Aberto a solicitação de Cadastro de Fornecedor Nº ";

        hAPI.setTaskComments(usuario, numProcesso, 0, mensagem);

        // Adiciona na tabela paiXfilho
        var childData = new java.util.HashMap();
        childData.put("cgcFornVenc", arrayFornecedor[indiceCadastro].cgc);
        //childData.put("numSubProcesso", numSubProcesso);
        hAPI.addCardChild("tbFornVenc", childData);
        hAPI.setCardValue("indiceCadastro", indiceCadastro + 1);

    }

    hAPI.setCardValue("iniciarCadastro", "false");
};

function dataAtual() {
    var data = new Date;
    var dia = data.getDate() < 10 ? '0' + data.getDate() : data.getDate();
    var mes = (data.getMonth() + 1) < 10 ? '0' + (data.getMonth() + 1) : (data.getMonth() + 1);
    var ano = data.getFullYear();

    return dia + '/' + mes + '/' + ano;
}

function servicetask403(attempt, message) {

    log.info("### compras.servicetask403 - Iniciado");
    var result = emitirPedidoCompra();
    return result;
}
function emitirPedidoCompra() {
    // MONTAR ESTRUTURA DE ITENS ANTES DE data CONFORME TABELA tbItens
    var gson = new com.google.gson.Gson();

    // Dados do cabeçalho
    var classeORC   = hAPI.getCardValue("classeOrcamentaria").split(' - ')[0];
    var classeVAL   = hAPI.getCardValue("classeValor"       ).split(' - ')[0];
    var filial      = hAPI.getCardValue("codFilial"         );
    var centroCusto = hAPI.getCardValue("centroDeCusto"     ).split(' - ')[0];
    var solicitante = hAPI.getCardValue("emailContatSolicitante"  ).split('@'  )[0];
    var dataPrevista = hAPI.getCardValue("dataNecessidade").split('/');
    var DataEmissao = new Date().toISOString().slice(0,10).replace(/-/g,"");

    // Obtenha o número de linhas da tabela
    var indexes = hAPI.getChildrenIndexes( "tbProd");
    var itens   = []; 
    for (var iREG = 0; iREG < indexes.length; iREG++) {
        var indice = indexes[iREG];
            dataPrevista = dataPrevista[2] + dataPrevista[1] + dataPrevista[0];

            var item = {};
                item["material"]     = hAPI.getCardValue("codProd___"    +indice);
                item["Quantidade"]   = hAPI.getCardValue("qtdProd___" 	 +indice);
                item["VlrUnit"]      = removeMascaraMonetaria(hAPI.getCardValue("valorProd___"  +indice));
                item["CCustos"]      = centroCusto;
                item["DataPrevista"] = dataPrevista;
                item["ClasseOrcam"]  = classeORC;
                item["ClasseValor"]  = classeVAL;
                item["Prioridade"]   = "N";
                item["Observacao"]   = hAPI.getCardValue("infoAdicionais___"        +indice);

            itens.push(item);
    }

    // NUMERO DO WORKFLOW
    var idFluig = ''+getValue("WKNumProces");
    var data = 
         {  "Filial"      : filial,
            "DataEmissao" : DataEmissao,
            "IdFluig"     : idFluig,
            "Solicitante" : solicitante,
            "items"       : itens
        };

    log.info("### compras.servicetask403 - data: ")
    log.dir(data);

    // http://oncoclinicasdo138877.protheus.cloudtotvs.com.br:4050/rest_des/WSRESTSC
    // Enviar data como raw
    var requestData = 
    {
        companyId     : "1",
        serviceCode   : "ProtheusSC",
        endpoint      : "/WSRESTSC",
        method        : "POST",
        timeoutService: "180",
        params        : data, 
        options: {
            mediaType: "application/json",
            encoding : "UTF-8"
        },
        headers: {
            "Content-Type": "application/json;charset=UTF-8"
        }
    };

    var xmlTransf = gson.toJson(requestData);

    log.info("### compras.servicetask403 - xmlTransf: ")
    log.dir(xmlTransf);

    var ClientService = fluigAPI.getAuthorizeClientService();
    var response      = ClientService.invoke( xmlTransf ); 
    var httpStatus    = response.getHttpStatusResult();
    var result        = response.getResult();

    log.info("### compras.servicetask403 - httpStatus: " + httpStatus);
    log.dir(result);

    if (httpStatus == 200 || httpStatus == 201) {
		log.info("#### compras.servicetask403 - Protheus - STATUS OK");
        // pegar o numero da sc criada no Protheus -  {"observacao":"Solicitacao de Compra numero: 001440 , Incluida com sucesso no ERP.","numero_sc":"001440","status":"true"} 

        var dados = JSON.parse(result);
        var numSC = dados.numero_sc;
        log.info("#### compras.servicetask403 - Protheus - NumSC: " + numSC);
        hAPI.setCardValue("txtNumeroProtheus", numSC);
        return true; 
	} else if (httpStatus >= 400) {
		throw "compras.servicetask403 - Protheus - Falha ao atualizar no Protheus: "+result;
    } 

}

function removeMascaraMonetaria(mask) {
    if (mask != undefined && mask != '') {
        mask = mask.replace('R$', '');
        mask = mask.replace(' ', '');
        //mask = mask = mask.replace(/[\.]/g, '');

        mask = mask.replace('.', '');
        mask = mask.replace('.', '');
        mask = mask.replace('.', '');

        mask = mask.replace(',', '.');
        return parseFloat(mask);
    } else {
        return 0.00;
    }
}
