/**
 *Envia anexo para uma solicitação
 *@codSolicitacao - Codigo da solicitção
 *@atividade - número da atividade em que será anexado o documento
 *@anexoB64 - Arquivo convertido em base64
 **/
function enviaAnexoSolicitacao(codSolicitacao, atividade, anexoB64, nomeFornecedorComp) {
 
    log.info("KAKAROTO INICIO DO ANEXO");
    log.info("PARAMETROS: cod "+ codSolicitacao + " - atividade - " + atividade +" - anexo - "+ anexoB64)
    log.info("KAKAROTO nome file: Pedido_Compras_" + nomeFornecedorComp)
    var nomeFornecedor = removeCaracteres(nomeFornecedorComp);
    // Obtém a instância do serviço 'WorkflowEngineService'
    var workflowEngineServiceProvider = ServiceManager.getServiceInstance("ECMWorkflowEngineService");
    var processAttachmentDtoArray = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.ProcessAttachmentDtoArray');
    var processAttachmentDto = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.ProcessAttachmentDto');
    var attachment = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.Attachment');
    var keyValueDto = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.KeyValueDto');
    var keyValueDtoArray = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.KeyValueDtoArray');
    var processTaskAppointmentDto = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.ProcessTaskAppointmentDto');
    var processTaskAppointmentDtoArray = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.ProcessTaskAppointmentDtoArray');
    var stringArray = workflowEngineServiceProvider.instantiate('br.com.oncoclinicas.fluig.StringArray');
    stringArray.getItem().add("15joqaztn156s2hg1491851925301")

    processAttachmentDto.setAttachmentSequence(1);
    processAttachmentDto.setCompanyId(1);
    processAttachmentDto.setFileName("Pedido_Compras_" + nomeFornecedor + ".pdf");
    processAttachmentDto.setDescription("Pedido_Compras_" + nomeFornecedor + ".pdf");
    processAttachmentDto.setNewAttach(true);
    processAttachmentDto.setVersion(1000);

    attachment.setAttach(true);
    attachment.setFileName("Pedido_Compras_" + nomeFornecedor + ".pdf");
    attachment.setFilecontent(java.util.Base64.getDecoder().decode(new java.lang.String(anexoB64).getBytes("UTF-8")));
    processAttachmentDto.getAttachments().add(attachment);
    processAttachmentDtoArray.getItem().add(processAttachmentDto);

    var idIntegrador = 'imwn2dyhbuywfa0f1522083830483';
    if (hAPI.getCardValue('ambiente') == 'devTst') {
        log.info("KAKAROTO ambiente dev");
        idIntegrador = 'imwn2dyhbuywfa0f1522083830483';
    }
    // Instancia o serviço
    var workflowEngineServiceLocator = workflowEngineServiceProvider.instantiate("br.com.oncoclinicas.fluig.ECMWorkflowEngineServiceService");
    var workflowEngineService = workflowEngineServiceLocator.getWorkflowEngineServicePort();
    var servico = workflowEngineService.saveAndSendTaskClassic("integrador.fluig@oncoclinicas.com",
        "hUbust*7",
        1,
        parseInt(codSolicitacao),
        parseInt(atividade),
        stringArray,
        "",
        idIntegrador,
        false,
        processAttachmentDtoArray,
        keyValueDtoArray,
        processTaskAppointmentDtoArray,
        true,
        0);

    //Verifica erro na integração do anexo
    for (var i = 0; i < servico.getItem().size(); i++) {
        log.info("KAKAROTO result: " + servico.getItem().get(i).getKey() + " - " + servico.getItem().get(i).getValue());
        if (servico.getItem().get(i).getKey() == "ERROR") {
            return "Erro para anexar o arquivo. - " + servico.getItem().get(i).getValue();
        }
    }

    return true;
}
//Busca o relatório pdf no Protheus
function getPdfPedidoProtheus(codPedido, codFilial) {
    var c1 = DatasetFactory.createConstraint('FILIAL', codFilial, codFilial, ConstraintType.MUST);
    var c2 = DatasetFactory.createConstraint('PEDIDO', codPedido, codPedido, ConstraintType.MUST);
    var ds_relatorio = DatasetFactory.getDataset('ds_pdfPedidoCompra', null, [c1, c2], null);
    if (ds_relatorio.getValue(0, "SUCESSO") == "true") {
        return {
            'status': 'true',
            'result': ds_relatorio.getValue(0, "PDF")
        }
    } else {
        return {
            'status': 'false',
            'result': ds_relatorio.getValue(0, "DESCRICAO")
        }
    }
}

function removeCaracteres(string) {
    string = string.split("/").join("");
    string = string.split("|").join("");
    string = string.split("<").join("");
    string = string.split(">").join("");
    string = string.split("*").join("");
    string = string.split(":").join("");
    string = string.split("“").join("");
    return string;
}

function GETSLA() {
    var active = getValue('WKNumState')

    if (active == 0 || active == getStartActive().getValue(0, "processStatePK.sequence")) {

        var codigo = hAPI.getCardValue('COD_SOLICITACAO');
        var dataSLA;
        // verifica se o processo foi iniciado com o campo COD_SOLICITACAO para consultar o sla
        // do contrario consulta o sla do processo default
        if (codigo != '' && codigo != undefined) {
            dataSLA = getSlaById(codigo)
            if (dataSLA.rowsCount <= 0) {
                dataSLA = getSlaDefault()
                hAPI.setCardValue('ISDEFAULT', '' + true)
            }
        } else {
            dataSLA = getSlaDefault();
            hAPI.setCardValue('ISDEFAULT', '' + true)
        }
        // valida se o chamado possui SLA
        if (dataSLA.rowsCount > 0) {

            // seta o valor id do documento e a versao
            hAPI.setCardValue('COD_SOLICITACAO', '' + dataSLA.getValue(0, 'documentid'))
            hAPI.setCardValue('VERSAO', '' + dataSLA.getValue(0, 'version'))
            // converte a string JsonSla em objeto e popula os campos
            dataSLA = dataSLA.getValue(0, 'jsonSLA')
            dataSLA = JSON.parse(dataSLA)
            hAPI.setCardValue('SLA_TOTAL', '' + dataSLA.slaTotal)


            for (var key in dataSLA.SLA) {
				
                var sla = dataSLA.SLA[key]
                hAPI.setCardValue('SLA' + sla.codAtividade, sla.sla)
            }

        } else {

            throw "Não foi encontrado um registro de SLA para essa solicitacao," +
                " portanto ela nao pode ser iniciada!!! Contate a equipe de melhorias Continuas"

        }

    }
}

// consulta o sla pelo codigo da solicitaçã
function getSlaById(id) {

    var cons = []
    cons.push(DatasetFactory.createConstraint('metadata#active', 'true', 'true', ConstraintType.MUST))
    cons.push(DatasetFactory.createConstraint('isActive', 'true', 'true', ConstraintType.MUST))
    cons.push(DatasetFactory.createConstraint('documentid', id, id, ConstraintType.MUST))
    return DatasetFactory.getDataset('ds_ficharioSLA', null, cons, null);
}
// consulta o SLA pelo codigo do processo
function getSlaDefault() {
    var codProcess = getValue("WKDef");

    var cons = []
    cons.push(DatasetFactory.createConstraint('metadata#active', 'true', 'true', ConstraintType.MUST))
    cons.push(DatasetFactory.createConstraint('idProcesso', codProcess, codProcess, ConstraintType.MUST))
    cons.push(DatasetFactory.createConstraint('isActive', true, true, ConstraintType.MUST))
    cons.push(DatasetFactory.createConstraint('isDefault', true, true, ConstraintType.MUST))
    return DatasetFactory.getDataset('ds_ficharioSLA', null, cons, null);

}
// consulta qual o Id da atividade inicial do fluxo()
function getStartActive() {
    var idProcess = getValue("WKDef")
    var versionProcess = getValue("WKVersDef")

    var cons = []
    cons.push(DatasetFactory.createConstraint('initialState', 'true', 'true', ConstraintType.MUST))
    cons.push(DatasetFactory.createConstraint('processStatePK.processId', idProcess, idProcess, ConstraintType.MUST))
    cons.push(DatasetFactory.createConstraint('processStatePK.version', versionProcess, versionProcess, ConstraintType.MUST))
    return DatasetFactory.getDataset('processState', null, cons, null);
}

function servicetask102() {
    myLog("SERVICO DE ANEXO");
    myLog("CAMPO PEDIDO: ", hAPI.getCardValue('listPedidoCompra'));
    if (hAPI.getCardValue('listPedidoCompra') == ''){
        throw "Nenhum pedido de compra encontrado no fluig para anexar."
    }else{
        var pedidosGerados = JSON.parse(hAPI.getCardValue('listPedidoCompra'));
        for (var nomeFornecedor in pedidosGerados) {
            if(!existeAnexoByName("Pedido_Compras_" + nomeFornecedor + ".pdf")){
                anexaPdfFluig(pedidosGerados[nomeFornecedor], nomeFornecedor);
            }
        }
    }
}
function anexaPdfFluig(codPedido, nomeFornecedor) {
    //BUSCA O ANEXO DO PEDIDO
    var codSolicitacao = hAPI.getCardValue('codSolicitacao');
    var codFilial = hAPI.getCardValue('codFilial');
    var resultPdfProtheus = getPdfPedidoProtheus(codPedido, codFilial);
    myLog('RESULT PDF ', resultPdfProtheus.result);
    if (resultPdfProtheus.status != 'true') {
        throw "Erro para buscar o realtório do pedido de compras no Protheus \n" + resultPdfProtheus.result;
    } else {
        myLog("PARM ENVIA ANEXO: ", codSolicitacao + pedidoCompras + nomeFornecedor)
        //ENVIA O ANEXO PARA O FLUIG
        var statusAnexo = enviaAnexoSolicitacao(codSolicitacao, anexaSolicitacao, resultPdfProtheus.result, nomeFornecedor + " - " + codPedido);
        myLog('RESULT ANEXO FLUIG ', statusAnexo);
        if (statusAnexo != true) {
            throw statusAnexo + ".";
        } else {
            myLog("PEDIDO GERADO: ", codPedido);
        }
    }
}

function existeAnexoByName(nomeAnexo){
    var attachments = hAPI.listAttachments();
    var existeAnexo = false;
    var nomePossivelAnexo = nomeAnexo;
    for (var i = 0; i < attachments.size(); i++) {
        var attachment = attachments.get(i);
        if (attachment.getDocumentDescription() == nomePossivelAnexo) {
            existeAnexo = true;
        }
    }
    return existeAnexo;
}

